mapper.insert:

		insert into PP_Reservation (
		res_num
		, mem_id
		, suk_num
		, res_resdate                     -- 예약한 시간
        , res_outdate 
        , res_pop 
        , res_parking  
        , res_pet 
        , res_message 
        , res_price
		)
		values (
		PP_reservation_seq.nextval
		, #{mem_id}
		, #{suk_num}
		, TO_DATE(#{res_resdate},'YYYY-MM-DD')       
        , TO_DATE(#{res_outdate} ,'YYYY-MM-DD') 
        , #{res_pop} 
        , #{res_parking}  
        , #{res_pet} 
        , #{res_message} 
        , #{res_price}

		)

mapper.update:
update PP_Reservation 
           set
		 res_resdate=TO_DATE(#{res_resdate},'YYYY-MM-DD')               
        , res_outdate=TO_DATE(#{res_outdate} ,'YYYY-MM-DD') 
        , res_pop= #{res_pop} 
        , res_parking= #{res_parking} 
        , res_pet= #{res_pet} 
        , res_message= #{res_message} 
        , res_price= #{res_price}
where 
suk_num=#{suk_num}
